$(()=> {
  $('.hamburger-menu').on('click', function() {
    $('.mobile-menu').css('transform', 'translateX(0%)');
  });

  $('.close-button').on('click', ()=> {
    $('.mobile-menu').css('transform', 'translateX(100%)');
  });
	
});

$("#cv").change(function() {
	//e.stopPropagation();
    var filename = $(this).val().split('\\');
	if(($('.file-800 span')).length == 0){
       $('.file-800').append('<span>' + filename[filename.length - 1] + '</span>');
	}
	else {
		$(".file-800 span").remove();
		$('.file-800').append('<span>' + filename[filename.length - 1] + '</span>');
	}
  });

$("#contact-btn").on('click', (events) => {
  events.preventDefault();
  var name = $("#name").val();
  var role = $("input[name='role']:checked").val();
  var own = $("input[name='own']:checked").val();
  var looking = $("input[name='looking']:checked").val();
  //var mode = $("input[name='contact']:checked").val();
  var contact = $("#contact1").val();
	var contact2 = $("#contact2").val();
  var data = 'name=' + name + '&role=' + role + '&own=' + own + '&looking=' + looking + '&contact=' + contact + '&contact2=' + contact2;
  if (name && role && own && looking && contact && contact2) {
    $.ajax({
      type: "POST",
      url: "https://palpx.com/wp-content/themes/palpx-website/email.php",
      data: data,
      header: { "Access-Control-Allow-Origin": "*" },
      error: (req, err) => {
        alert(err);
      },
      success: () => {
         window.location.href="https://palpx.com/thank-you/";
      }
    });
  } else {
      alert('Please fill in all the form fields');
  }
});
